
public class Sommet {

	private int degree;
	private int numero;
	
	
	public Sommet(int numero) {
		// TODO Auto-generated constructor stub
		this.numero = numero;
		
	}

	// getters et setters
	public int getDegree() {
		return degree;
	}


	public void setDegree(int degree) {
		this.degree = degree;
	}


	public int getNumero() {
		return numero;
	}


	public void setNumero(int numero) {
		this.numero = numero;
	}

	
}
